(function ($) {
	var classId = {
		$globalAudio: $("#globalAudio"),
		$u_globalAudio: $(".u-globalAudio-f"),
		$coffeeSteamBox: $(".coffee-steam-box")
	};
	H.music = {
		$p:true,
		init: function() {
			this.palyFn();
			this.piaoFn();
		},
		palyFn: function() {
			classId.$globalAudio.click(function() {
				if(H.music.$p) {
					$("#globalAudioPlayer").get(0).play();
					classId.$u_globalAudio.addClass("music");
					H.music.piaoFn();
					H.music.$p=false;
				}else {
					$("#globalAudioPlayer").get(0).pause();
					classId.$u_globalAudio.removeClass("music");
					classId.$coffeeSteamBox.empty();
					H.music.$p=true;
				}
			});
		},
		piaoFn: function() {
			var h = "<span></span><span></span><span></span>";
			classId.$coffeeSteamBox.append(h);
		}
	}
	H.music.init();
	
})(Zepto);